        AND manager_id = #{blog.managerId,jdbcType=INTEGER}


        AND blog_type = #{blog.blogType,jdbcType=INTEGER}


        AND blog_state = #{blog.blogState,jdbcType=TINYINT}


order by sort_index desc, create_time desc limit #{index}, #{rows}

